import axios from 'axios';

class SlotDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie : this.props.slot._movie
        }
        this.GetSlotInfoAPIHandler = this.GetSlotInfoAPIHandler.bind(this)
    }

    GetSlotInfoAPIHandler = () => {
        console.log(this.props.slot)
    }

    componentDidMount() {
        this.GetSlotInfoAPIHandler();
    }

    render() {
        if (this.props.slot._id) {
            console.log('movie exists')
            return (
                <div>
                    <h1>Movie id : {this.state.movie}</h1>
                    <h2>Date : {this.props.slot.day}</h2>
                    <h2>Seat Prices : </h2>
                    <h3>Gold - {this.props.slot.seatInfo.A.price}</h3>
                    <h3>Balcony - {this.props.slot.seatInfo.B.price}</h3>
                    <button>Show seating arrangements</button>
                </div>
            )
        }
        else {
            return (
                <div>
                    <h1>no movie added in this slot</h1>
                    <button>Add movie in this slot</button>
                </div>
            )
        }
    }
}
export default SlotDetails
