import axios from 'axios';

class SlotDetails extends Component {
    constructor(props) {
        super(props);
    state = {
        getSlotInfoBody: {
            day: "2020-06-07"
        },
        postSlotInfoBody: {
            movieid: '',
            priceInfo: {
                A: '',
                B: ''
            },
            bookingInfo: [
                {
                    day: '',
                    status: '',
                    slot: ''
                }
            ]
        },
        getSlotInfo : {
            _id : '',
            _movie : '',
            _slot : '',
            _theatre : '',
            day : '',
            seatInfo : {
                A : {
                    availability : [],
                    price : ''
                },
                B : {
                    availability : [],
                    price : ''
                }
            }
        }
    }

    GetSlotInfoAPIHandler = (slotid, theatreid) => {
        console.log('get slotInfo done!')
        //console.log(theatreid, slotid, this.state.getSlotInfoBody)
        axios.defaults.headers.common['Authorization'] = localStorage.getItem("token");
        axios.post(`http://localhost:3000/theatres/${theatreid}/${slotid}`, this.state.getSlotInfoBody)
            .then(response => {
                console.log(response.data)
                if(response.data){
                this.setState({
                    getSlotInfo : {
                        _id : response.data._id,
                        _movie : response.data._movie,
                        _slot : response.data._slot,
                        _theatre : response.data._theatre,
                        day : response.data.day,
                        seatInfo : {
                            A : {
                                availability : response.data.seatInfo.A.availability,
                                price : response.data.seatInfo.A.price
                            },
                            B : {
                                availability : response.data.seatInfo.B.availability,
                                price : response.data.seatInfo.B.price
                            }
                        }
                    }

                })}
                console.log(this.state)

            }).catch((e) => alert(e));
    }
}

    componentDidMount() {
        // var selectedSlotId = this.props.match.params.SlotId;
        // var selectedTheatreId = this.props.match.params.TheatreId;
        // //console.log(selectedTheatreId, selectedSlotId)
        // this.GetSlotInfoAPIHandler(selectedSlotId, selectedTheatreId);
    }

    render() {

         if(this.state.getSlotInfo._id)
            {
                return (
                <h1>HEYLOO DID</h1>
                )
            }
            else 
            {
                return(
                    <h1>no movie booked for this slot</h1>
                )
            }
    }
}
export default SlotDetails
