import React, {Component } from 'react';
import '../stylesheets/MovieDetailPage.css';
import deadpool from "../images/deadpool.jpg";

import {Link} from 'react-router-dom';

class MovieDetailPage extends Component {
    componentDidMount() {
        console.log(this.props);
    }
    render() {
        return (
            <div className="MovieDetail">

                <div className="Movieposterbox">
                    <img src={deadpool} alt="my movie poster" className="Movieposter"/>
                </div>

                <div className="Movie-info-box">
                    <h1 className="Movietitle">DEADPOOL</h1>
                    <p className="Movie-Genre">Genre</p>
                    <p>U , Lannguage, 2D</p>
                    <p>User rating. Critic's Rating</p>
                    <p>You rate it</p>
                    <button>Book Tickets</button>
                    <div>
                        <ul>
                            <li><Link to = {{
                            pathname : this.props.match.url + '/Summary'}
                            }>Summary</Link></li>
                            <a href="/User's Reviews">User's Reviews</a>
                            <a href="/Critic's Reviews">Critic's Reviews</a>
                        </ul>
                    </div>
                    <p className="Movie-Description">Ajax, a twisted scientist, experiments on Wade Wilson, a mercenary, to cure him of cancer and give him healing powers. However, the experiment leaves Wade disfigured and he decides to exact revenge.</p>
                    <p>Date of Release :</p>
                    <p>Duration : </p>
                    <p>Starring : Ryan Reynolds, Morena Baccarin, Ed Skrein, T. J. Miller</p>
                    <p>Directed : Tim Miller</p>
                </div>

            </div>
        )
    }
}

export default MovieDetailPage
