import React, { Component } from 'react';
import axios from 'axios';

import styles from './stylesheets/AddTheatre.module.css'

class AddTheatre extends Component {
    constructor(props) {
        super(props);
        this.state = {
            theatreName: '',
            location: {
                country: '',
                state: '',
                city: '',
                street: ''
            },
            seatInfo: {
                A: '6',
                B: '12'
            },
            slotInfo: [],
        }
        this.updateTheatreState = this.updateTheatreState.bind(this);
        this.AddTheatreAPIHandler = this.AddTheatreAPIHandler.bind(this);
    }

    updateTheatreState = (event) => {
        console.log(event.target.value)
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    AddTheatreAPIHandler = (e) => {
        //e.preventDefault()
        console.log('theatre added!')
        console.log(this.state)
        // const movie = {
        //     name: this.state.movieName
        // };
        // console.log(movie);
        // axios.post('http://localhost:3001/addmovie', movie)
        //     .then(response => {
        //         console.log('movie added!')
        //         console.log(response.data)
        //     }).catch((e) => alert(e));
        // this.setState({
        //     movieName: '',
        // })
    }
    render() {
        return (
            <div className={styles.AddTheatre}>
                <h1 className={styles.heading} >Add Theatre : </h1>


                <p className={styles.task}> Enter the Theatre Name : </p>
                <input className={styles.inputbox} type="text" placeholder="Theatre Name"
                    name="theatreName" value={this.state.theatreName} onChange={this.updateTheatreState} />


                <p className={styles.task}> Enter the Location : </p>
                <input className={styles.inputbox} type="text" placeholder=" Enter Street"
                    name="street" value={this.state.location.street} onChange={this.updateTheatreState} />
                <br></br>
                <input className={styles.inputbox} type="text" placeholder=" Enter City Name"
                    name="city" value={this.state.location.city} onChange={this.updateTheatreState} />
                <br></br>
                <input className={styles.inputbox} type="text" placeholder=" Enter State Name"
                    name="state" value={this.state.location.state} onChange={this.updateTheatreState} />
                <br></br>
                <input className={styles.inputbox} type="text" placeholder=" Enter Country Name"
                    name="country" value={this.state.location.country} onChange={this.updateTheatreState} />


                <p className={styles.task}> Add ShowTimes : </p>
                <input className={styles.inputbox} type="text" placeholder="put 7:30 as 0730"
                    name="slotInfo" value={this.state.slotInfo} onChange={this.updateTheatreState} />


                <br></br>
                <input className={styles.submitbutton} type="submit" onClick={this.AddTheatreAPIHandler} />
            </div>


        )
    }
}

export default AddTheatre;
