import React, {Component } from 'react';
import '../stylesheets/MovieDetailPage.css';
import deadpool from "../images/deadpool.jpg";

import {BrowserRouter} from 'react-router-dom';
import {Link,Route} from 'react-router-dom';
import MovieDetailSummary from "./MovieDetailSummary";

class MovieDetailPage extends Component {
    componentDidMount() {
        console.log(this.props);
    }
    render() {
        return (
            <BrowserRouter>
            <div className="MovieDetail">

                <div className="Movieposterbox">
                    <img src={deadpool} alt="my movie poster" className="Movieposter"/>
                </div>

                <div className="Movie-info-box">
                    <h1 className="Movietitle">DEADPOOL</h1>
                    <p className="Movie-Genre">Genre</p>
                    <p>U , Language, 2D</p>
                    <div className="Movie-rating-box">
                        <div className="Movie-Rating">
                            <p>3.9</p>
                            <h3>User's Rating</h3>
                        </div>
                        <div className="Movie-Rating">
                            <p>3.9</p>
                            <h3>Critic's Rating</h3>
                        </div>
                        <div className="Movie-Rating">
                            <p>3.9</p>
                            <h4>You rate it</h4>
                        </div>
                   </div>
                    <button>Book Tickets</button>
                    <div>
                        <ul>
                            <li><Link to = {{
                            pathname : this.props.match.url + '/Summary'}
                            }>Summary</Link></li>
                            <li><Link to = {{
                                pathname : this.props.match.url + "/User's Reviews"}
                            }>User's Reviews</Link></li>
                            <li><Link to = {{
                                pathname : this.props.match.url + "/Critic's Reviews"}
                            }>Critic's Reviews</Link></li>
                        </ul>
                    </div>
                    <Route path="/MovieDetail/Summary" exact component = {MovieDetailSummary} />
                </div>

            </div>
            </BrowserRouter>
        )
    }
}

export default MovieDetailPage
