import React, { Component } from 'react';

import axios from 'axios';

class SlotDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: this.props.slot._movie,
            date: this.props.slot.day,
            seatInfo: {
                A: this.props.slot.seatInfo.A.price,
                B: this.props.slot.seatInfo.B.price
            }
        }
        this.updateSlotState = this.updateSlotState.bind(this);
        this.GetSlotInfoAPIHandler = this.GetSlotInfoAPIHandler.bind(this)
    }

    GetSlotInfoAPIHandler = () => {
        console.log(this.props.slot)
    }

    updateSlotState = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    componentDidMount() {
        this.GetSlotInfoAPIHandler();
    }

    render() {
        // if (!this.props.slot._id) {
        //     return (
        //         <div>
        //             <h1>no movie added in this slot</h1>
        //             <button>Add movie in this slot</button>
        //         </div>
        //     )
        // }

        return (
            <div>
                <h1>Movie name/id : </h1>
                <input type="text" placeholder="Movie Name"
                    name="movie" value={this.state.movie} onChange={this.updateSlotState}></input>

                {/* <h1>Movie id : {this.state.movie}</h1> */}
                <h2>Date : {this.state.date}</h2>
                <h2>Seat Prices : </h2>
                <h3>Gold - {this.state.seatInfo.A}</h3>
                <h3>Balcony - {this.state.seatInfo.B}</h3>
                <button>Show seating arrangements</button>
            </div>
        )
    }
}

export default SlotDetails
