import React, {Component } from 'react';
import '../stylesheets/MovieFilter.css';

const CSSTransitionGroup = React.addons.TransitionGroup;
const TransitionGroup = React.addons.TransitionGroup;

class Filter extends React.Component{
    constructor(props) {
        super(props);
        this.state = { visible: false };
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick() {
        this.setState({ visible: ! this.state.visible });
    }

    render() {
        return (
        <div>
            <button onClick={this.handleClick}>{this.state.visible ? 'Slide up' : 'Slide down'}</button>
            <CSSTransitionGroup transitionName="example">
                { this.state.visible ? <div className='panel' /> : null }
            </CSSTransitionGroup>
        </div>

        )}
}

export default Filter;
