import React, {Component } from 'react';
import styles from './stylesheets/AddTheatre.module.css'
class AddTheatre extends Component {
    constructor(props) {
        super(props);
        this.state = {
            theatreName: '',
            location : {
                country : '',
                state : '',
                city : '',
                street : ''
            },
            seatInfo : {
                A : '6',
                B : '12'
            },
            slotInfo : [],
        }
        this.updateTheatreState = this.updateTheatreState.bind(this);
        this.AddTheatreAPIHandler = this.AddTheatreAPIHandler.bind(this);
    }
    updateTheatreState = (event) => {
        this.setState({
            [name.event.target]: event.target.value
        });
    }

    AddMovieAPIHandler = (e) => {
        //e.preventDefault()
        const movie = {
            name: this.state.movieName
        };
        console.log(movie);
        axios.post('http://localhost:3001/addmovie', movie)
            .then(response => {
                console.log('movie added!')
                console.log(response.data)
            }).catch((e) => alert(e));
        this.setState({
            movieName: '',
        })
    }
    render() {
        return (
            <div className = {styles.AddTheatre}>
            <h1 className={styles.heading} >Add Theatre : </h1>
            <p className={styles.task}> Enter the Theatre Name : </p>
            
            <input className={styles.inputbox}
            type="text" name="Enter Theatre" placeholder="Theatre Name"/>
            <input className={styles.submitbutton} 
            type="submit" value="Add Theatre"/>
            </div>
            
            
        )
    }
}

export default AddTheatre;
