import React, { Component } from 'react';
import { BrowserRouter} from 'react-router-dom';
import { Provider as AlertProvider } from 'react-alert'
import AlertTemplate from 'react-alert-template-oldschool-dark'

import UserApp from "./Customer/UserApp";
import AdminPage from "./Admin/AdminPage"
import VendorApp from './Theatre Owner/VendorApp';

const options = {
    position: 'bottom center',
    timeout: 5000,
    offset: '30px',
    transition: 'scale'
  }

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userType: ""
        };
        this.safelyParseJSON = this.safelyParseJSON.bind(this)
    }

    safelyParseJSON = (json) => {
        var parsed
        try {
            parsed = JSON.parse(json)
        } catch (e) {
            // Oh well, but whatever...
        }
        return parsed // Could be undefined!
    }

    componentDidMount() {
        const currentUserStorage = localStorage.getItem("currentUser");
        const currentUser = this.safelyParseJSON(currentUserStorage);
        if (currentUser) {
            this.setState({ userType: currentUser.userType })
        }
    }

    render() {
        const alert = useAlert()
        if (this.state.userType === 'vendor')
            return (
                <AlertProvider template={AlertTemplate} {...options}>
                <BrowserRouter>
                    <div>
                        <VendorApp />
                        alert.show('look an alert!')
                    </div>
                </BrowserRouter>
                </AlertProvider>
            )
        else if (this.state.userType === 'admin')
            return (
                <BrowserRouter>
                    <div>
                        <AdminPage />
                    </div>
                </BrowserRouter>
            )
        else
            return (
                <BrowserRouter>
                    <div>
                        <UserApp />
                    </div>
                </BrowserRouter>
            )

    }
}


export default App;
