import React, { Component } from 'react';
import axios from 'axios';

import styles from './stylesheets/userLogin.module.css';

class UserSignup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            firstname: 'nymika',
            lastname: '',
            email: 'effve s@gmail.com',
            mobile: '',
            password: '678909876',
            repassword: ''
        }
        this.updateUserState = this.updateUserState.bind(this);
        this.createUserAPIHandler = this.createUserAPIHandler.bind(this);
    }

    updateUserState = (event) => {
        //console.log('state updating')
        this.setState({
            firstname: event.target.firstname,
            lastname: event.target.lastname
        });
        console.log(this.state.firstname);
    }

    createUserAPIHandler = () => {
        const user = {
            email: this.state.email,
            password: this.state.password,
            name: this.state.firstname
        }
        axios.post('localhost:3001/users/signup', user)
            .then(response => {
                console.log(response)
            }).catch((e) => console.log(e));
    }

    render() {
        return (
            <div className={styles.loginbox}>
                <h1 className={styles.heading}> SignUp Here!</h1>
                <div className={styles.signup}>
                    <div className={styles.myName}>
                        <input type="text" value={this.state.firstname}  className={styles.inputbox} id="myname" placeholder="First Name"></input>
                        <input type="text" value={this.state.lastname} className={styles.inputbox} id="mylastname" placeholder="Last Name"></input>
                    </div>

                    <div className={styles.email}>
                        <input className={styles.inputbox} value={this.state.email} type="text" placeholder="email@gmail.com"></input>
                    </div>

                    <div className={styles.mymobile}>
                        <input type="text" className={styles.inputbox} id="mobile" placeholder="Mobile"></input>
                    </div>

                    <div>
                        <p className={styles.genderlabel}>Enter Password</p>
                        <input type="text" value={this.state.password} className={styles.inputbox} id="pass" placeholder="enter password"></input>
                        <input type="text" className={styles.inputbox} id="repass" placeholder="re-enter password to confirm"></input>
                    </div>

                    <button type="submit" className={styles.submitbtn} onClick={this.createUserAPIHandler}>Create new account</button>
                </div>
            </div >
        )
    }
}
export default UserSignup;
