import React, { Component } from 'react';
import { BrowserRouter, Link, Route, Switch } from 'react-router-dom';

import UserApp from "./Customer/UserApp";
import TheatreOwnerPage from "./Theatre Owner/TheatreOwnerPage"
import AdminPage from "./Admin/AdminPage"

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: ""
        };
    }

    componentDidMount() {
        const currentUserStorage = localStorage.getItem("currentUser");
        const currentUser={}
        if(currentUserStorage)
        {
            currentUser=JSON.parse(currentUserStorage);
        }
        this.setState({ user: currentUser })
    }
    render() {
        return (
            <BrowserRouter>
                <div>
                    <p className="App-intro">HI  {this.state.apiResponse} {this.state.user}</p>
                    <ul>
                        <li><Link to="/">Customer</Link></li>
                        <li><Link to={{
                            pathname: '/TheatreOwnerPage'
                        }}>Theatre Owner Page</Link></li>
                        <li><Link to="/AdminPage">Admin Page</Link></li>
                    </ul>
                    </div>
                <Switch>
                    <Route path="/TheatreOwnerPage" component={TheatreOwnerPage} />
                    <Route path="/AdminPage" component={AdminPage} />
                    <Route path="/" component={UserApp} />
                </Switch>
            </BrowserRouter>
        )
    }
}


export default App;
